pragma solidity 0.8.24;

import { Ownable } from "@openzeppelin/contracts/access/Ownable.sol"; 

abstract contract Agent is Ownable {
    error InvalidAgentAddress();
    error AgentAddressIsTheSame();

    address private agent;

    // Platform fee
    uint public makerRate;

    constructor(address controller, address newAgent) Ownable(controller){
        agent = newAgent;
    }

    function replaceAgent(address newAgent) public onlyOwner {
        if(newAgent == address(0)) revert InvalidAgentAddress();
        if(newAgent == agent) revert AgentAddressIsTheSame();
        agent = newAgent;
    }

    /**
     * @dev Adjust fee
     * @param newRate : New fee
     */
    function setFee(uint16 newRate) public onlyOwner {
        makerRate = newRate;
    }
}","keccak256":"0x99825bdcb5c8f8a2a374fd3dbe6826e829796162e2a1b80cfcf80fade54804c1","license":"MIT"},"contracts/peripherals/Contributor.sol":{"content":"// SPDX-License-Identifier: MIT

pragma solidity 0.8.24;

import { Common } from "../apis/Common.sol";

abstract contract Contributor {
    error OnlyContributorIsAllowed();
    error OnlyNonContributorIsAllowed();

    //Mapping of unit to position to contributors
    mapping (uint256 => mapping(uint => Common.Contributor))public contributors;

    // Mapping of unit to userCount
    mapping (uint256 => uint) public userCounts;

    // Mapping of user to unit to position
    mapping (address => mapping(uint256 => uint)) public positions;

    /**
     * @dev Only contributor in a pool is allowed
     * @param user : Target
     * @param unit : Unit Contribution
    */
    function _onlyContributor(address user, uint256 unit) internal view {
        if(!_getProfile(user, unit).isMember) revert OnlyContributorIsAllowed();
    }

    /**
     * @dev Only Non contributor in a pool is allowed
     * @param user : Target
     * @param unit : Unit Contribution
     */
    function _onlyNonContributor(address user, uint256 unit) internal view {
        if(_getProfile(user, unit).isMember) revert OnlyNonContributorIsAllowed();
    }

    /**
     * @dev returns user's profile status in a pool
     * @param user : Target address
     * @param unit : Unit contribution
     */
    function _getProfile(address user, uint256 unit) internal view returns(Common.Contributor memory _isMember) {
        uint pos = positions[user][unit];
        _isMember = contributors[unit][pos];
    }

    /**
     * @dev returns user's profile status in a pool
     * @param unit : Unit contribution
     */
    function _getExpected(uint256 unit) internal view returns(Common.Contributor memory _isMember) {
        uint pos = userCounts[unit];
        _isMember = contributors[unit][pos];
    }

    /**
     * @dev returns user's profile status in a pool
     * @param user : Target address
     * @param unit : Unit contribution
     * @param date : Date/timestamp
     */ 
    function _setTurnTime(address user, uint256 unit, uint64 date) internal {
        uint pos;
        if(user == address(0)){
            pos = userCounts[unit];
        } else {
            pos = positions[user][unit];
        }
        contributors[unit][pos].turnTime = date;
    }

    /**
     * @dev returns user's contributorship status in a pool
     * @param newProfile : Target profile
     * @param unit : Unit contribution
     */
    function _addContributor(uint256 unit, Common.Contributor memory newProfile) internal {
        uint pos = userCounts[unit];
        userCounts[unit] = pos + 1;
        contributors[unit][pos] = newProfile;
        positions[newProfile.id][unit] = pos;
    }

    function _incrementUserCount(uint256 unit) internal {
        userCounts[unit] += 1;
    }

    /**
     * @dev returns user's contributorship status in a pool
     * @param user : Target address
     * @param unit : Unit contribution
     */
    function _removeContributor(address user, uint256 unit) internal {
        uint pos = positions[user][unit];
        delete contributors[unit][pos];
        delete positions[user][unit];
    }

    function _getUserCount(uint256 unit) internal view returns(uint _count) {
        _count = userCounts[unit];
    }

    function _resetUserCount(uint256 unit) internal {
        userCounts[unit] = 0;
    }

    function _swapFullProfile(
        uint256 unit,
        address actCaller,
        Common.Contributor memory expcData
    )
        internal
        returns(Common.Contributor memory aCData) 
    {
        uint aSlot = positions[actCaller][unit];
        uint eSlot = positions[expcData.id][unit];
        aCData = contributors[unit][aSlot];
        aCData.turnTime = expcData.turnTime;
        expcData.turnTime = 0;
        contributors[unit][eSlot] = aCData;
        contributors[unit][aSlot] = expcData;
        positions[actCaller][unit] = eSlot;
        positions[expcData.id][unit] = aSlot;
        contributors[unit][aSlot].turnTime = expcData.turnTime;
    }
}","keccak256":"0x7ea0455d4835b986ab85beb4e3feaea776a93b69678742df39a6cec7e579c82a","license":"MIT"},"contracts/peripherals/CreatePool.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.24;

import { Agent } from "./Agent.sol";
import { Pools } from "./Pools.sol";
import { Safe } from "./Safe.sol";
import { Contributor } from "./Contributor.sol";
import { Common } from "../apis/Common.sol";
import { IERC20 } from "../apis/IERC20.sol";
import { TokenInUse } from "./TokenInUse.sol";
import { Point } from "./Point.sol";
import { Utils } from "../libraries/Utils.sol";

contract CreatePool is Agent, Pools, Safe, Point, TokenInUse, Contributor {
    // using SafeMath for uint256;
    using Utils for *;

    error InvalidSafe();
    error PoolIsTaken();
    error AddressMustBeArrayOfOneAddress();
    error CollaterlCoverageTooLow();
    error DurationExceed720HoursOrIsZero();
    error UserArrayExceedOne();
    error AddingUserEnded();
    error TokenBalanceInSafeNotTally();

    constructor(
        address controller, 
        address newAgent, 
        IERC20 _token
    ) 
        Agent(controller, newAgent) TokenInUse(_token)
    {}

    function _createPool(
        address newSafe, 
        address[] memory users, 
        uint256 unit,
        uint8 quorum,
        uint16 intRate,
        uint16 durationInHours,
        uint24 colCoverage,
        Common.Router router
    ) internal {
        if(users.length > 1) revert AddressMustBeArrayOfOneAddress();
        bytes32 ownerHash = _setOwnersHash(users, unit, users[0]);
        if(_getSafe(unit).id == address(0)) {
            if(newSafe == address(0)) revert InvalidSafe();
            require(IERC20(_getToken()).balanceOf(newSafe) >= unit, "Low Liq");
            _setSafe(Common.Safe(newSafe, ownerHash), unit); 
        }
        if(_getPool(unit, Common.UnitStatus.RECORD).lInt.status == Common.Status.TAKEN) revert PoolIsTaken();
        if(colCoverage < 100) revert CollaterlCoverageTooLow();
        if(durationInHours == 0 || durationInHours > 720) revert DurationExceed720HoursOrIsZero();      
        unchecked {
            _setPool(
                Common.Pool(
                    users[0],
                    address(0),
                    Common.LowInt(quorum, colCoverage, durationInHours * 1 hours, Common.Status.TAKEN, router, Common.Stage.JOIN),
                    Common.BigInt(unit, unit, _getRecordId()),
                    (unit * quorum).computeInterestsBasedOnDuration(intRate, uint24(durationInHours))
                ),
                unit,
                Common.UnitStatus.CURRENT
            );
        }
        _incrementUserCount(unit);
    }

    function _setOwnersHash(
        address[] memory addrs, 
        uint256 unit, 
        address admin
    ) internal returns(bytes32 _hash) {
        uint size = addrs.length;
        for(uint i = 0; i < size; i++) {
            address addr = addrs[i];
            if(addr == admin){
                _hash = keccak256(
                    abi.encodePacked(
                        _hash,
                        addr
                    )
                );
            } else { 
                _hash = keccak256(
                    abi.encodePacked(
                        _hash,
                        addr,
                        unit
                    )
                );
            }
            _setPoint(addr, 0, 2);
            _addContributor(unit, Common.Contributor(addr == admin, 0, 0, 0, 0, addr, true));
        }
    }

     function addUserToPool(uint256 unit, address[] memory user) public returns(bool) {
        Common.Pool memory _p = _getPool(unit, Common.UnitStatus.CURRENT);
        Common.Contributor memory _c = _getProfile(user[0], unit);
        _setOwnersHash(user, unit, address(0));
        unchecked {
            _p.bigInt.currentPool += unit;
        }
        if(_p.lInt.stage != Common.Stage.JOIN) revert AddingUserEnded();
        if(user.length > 1) revert UserArrayExceedOne();
        if(IERC20(_getToken()).balanceOf(_getSafe(unit).id) < _p.bigInt.currentPool) revert TokenBalanceInSafeNotTally();
        if(_p.lInt.router == Common.Router.PERMISSIONLESS) require(!_c.isMember, "User exist");
        else require(_c.isMember && !_c.sentQuota, "Already sent quota");
        _incrementUserCount(unit);
        _addContributor(unit, Common.Contributor(true, 0, 0, 0, 0, user[0], true));
        if(_getUserCount(unit) == _p.lInt.quorum) {
            _p.lInt.stage = Common.Stage.GET;
            _resetUserCount(unit);
            _setTurnTime(_p.admin, unit, Utils._now());
            // _p.beneficiary = _p.admin;
        }
        _setPool(_p, unit, Common.UnitStatus.CURRENT);

        return true;
    }

    function setToken(IERC20 newToken) public onlyOwner {
        _replaceToken(newToken);
    }
    
}
","keccak256":"0xa5e486cacccd7b4e61bc992a00024884ca43b5606233467fe0bcd7f129e0d16c","license":"MIT"},"contracts/peripherals/Oracle.sol":{"content":"// SPDX-License-Identifier: MIT

