pragma solidity 0.8.24;

import { Common } from "../apis/Common.sol";

abstract contract Point {

    // Mapping of unit contributors to Point struct
    mapping (address => Common.Point) private points;

    /**
     * @dev Returns the point
     * @param user : Contributor
    */
    function _getPoint(address user) internal view returns(Common.Point memory point){
        point = points[user];
    }

    /**
     * @dev Returns the point
     * See _getPoint
    */
    function getPoint(address user) public view returns(Common.Point memory){
        return _getPoint(user);
    }

    /**
     * @dev Returns the point information
     * @param user : Unit contribution
     * @param contributor : Point earned as contributor;
     * @param creator : Point earned as a creator
    */
    function _setPoint(address user, uint contributor, uint creator) internal virtual {
        if(contributor > 0) points[user].contributor += contributor;
        if(creator > 0) points[user].creator += creator;
    }
}","keccak256":"0x3478e950023d4d90c754e1345794f1d09d08a6ba8f45d8edd7541f4f0a88271d","license":"MIT"},"contracts/peripherals/Pools.sol":{"content":"// SPDX-License-Identifier: MIT

pragma solidity 0.8.24;

import { Common } from "../apis/Common.sol";

abstract contract Pools {
    uint public pastRecords;

    // Mapping of unit contribution to past pool
    mapping (uint256 => mapping( Common.UnitStatus => Common.Pool)) private pools; 

    /**
     * @dev Add pool to storage
     * @param pool : A new pool that just ended
     * @param unit : Unit contribution
     */
    function _setPool(Common.Pool memory pool, uint256 unit, Common.UnitStatus status) internal virtual {
        pools[unit][status] = pool;
    }

    /**
     * @dev Remove pool from storage
     * @param unit : Unit contribution
     */
    function _removePool(uint256 unit) internal virtual {
        delete pools[unit][Common.UnitStatus.CURRENT];
    }

    /**
     * @dev Get pool from storage
     * @param unit : Unit contribution
     */
    function _getPool(uint256 unit, Common.UnitStatus status) internal view returns(Common.Pool memory) {
        return pools[unit][status];
    }

    /**
     * @dev Get pool from storage
     * @param unit : Unit contribution
     */
    function isPoolAvailable(uint256 unit) public view returns(bool) {
        return pools[unit][Common.UnitStatus.CURRENT].lInt.status == Common.Status.AVAILABLE;
    }

    function _getRecordId() internal returns(uint rId) {
        rId = pastRecords;
        pastRecords ++;
    }
}","keccak256":"0xc716b492423376a0a7398cad040359c73106e99ce4716fb443e60896fd062ae6","license":"MIT"},"contracts/peripherals/Safe.sol":{"content":"// SPDX-License-Identifier: MIT

pragma solidity 0.8.24;

import { Common } from "../apis/Common.sol";

abstract contract Safe {

    // Mapping of unit contribution to Safe struct
    mapping (uint256 => Common.Safe) private safes;

    /**
     * @dev Returns the safe information
     * @param unit : Unit contribution
    */
    function _getSafe(uint256 unit) public view returns(Common.Safe memory safe){
        safe = safes[unit];
    }

    /**
     * @dev Returns the safe information
     * See _getSafe
    */
    function getSafe(uint256 unit) public view returns(Common.Safe memory){
        return _getSafe(unit);
    }

    /**
     * @dev Returns the safe information
     * @param unit : Unit contribution
     * @param safe : Safe Struct
    */
    function _setSafe(Common.Safe memory safe, uint256 unit) internal virtual {
        safes[unit] = safe;
    }
}","keccak256":"0x932c626fbbd57d72f8c60a0bbc680aa63d58b587395132a8dba775b6fc67c925","license":"MIT"},"contracts/peripherals/TokenInUse.sol":{"content":"// SPDX-License-Identifier: MIT

pragma solidity 0.8.24;

import { IERC20 } from "../apis/IERC20.sol";

abstract contract TokenInUse {
    error InvalidTokenAddress();
    error TokenAddressIsTheSame();

    IERC20 public token;

    constructor(IERC20 _token) {
        token = _token;
    }

    function _replaceToken(IERC20 newToken) internal virtual {
        if(address(newToken) == address(0)) revert InvalidTokenAddress();
        if(newToken == token) revert TokenAddressIsTheSame();
        token = newToken;
    }

    function _getToken() internal view returns(IERC20 _token) {
        _token = token;
    }

}","keccak256":"0x093df2e4c097473cb49ac6a815020237c4cc71f209f48eefe7de9cc030788d59","license":"MIT"}},"version":1}
